class Solution {
    public int removeElement(int[] nums, int val) {

        int val_occurrences = 0;
        int nums_len = nums.length - 1;
        for(int i=0, j=nums_len ; i<=j ;i++){
            if(nums[i] == val){
                while(i!=j && nums[j] == val){
                   val_occurrences++;
                    j--;
                }
                int temp = nums[i];
                nums[i] = nums[j];
                nums[j] = temp;
                val_occurrences++;
                j--;

            }
        }
        return nums.length - val_occurrences;
    }
}

