class Solution {
    public int romanToInt(String s) {

        int result = 0;

        for(int i = 0; i< s.length(); i++){
            int num = s.charAt(i);
            switch(num){
                case 'I':
                    num = 1;
                    break;
                case 'V':
                    num = 5;
                    break;
                case 'X':
                    num = 10;
                    break;
                case 'L':
                    num = 50;
                    break;
                case 'C':
                    num = 100;
                    break;
                case 'D':
                    num = 500;
                    break;
                case 'M':
                    num =1000;
                    break;
                default: num = 0;
            }

            if(i < s.length()-1){
