class Solution {
    public int romanToInt(String s) {
        Map<Character,Integer> numMap = Map.of(
            'I', 1,
            'V', 5,
            'X', 10,
            'L', 50,
            'C', 100,
            'D', 500,
            'M',1000
        );

        int result = 0;

        for(int i = 0; i< s.length(); i++){
            int num = numMap.get(s.charAt(i));
            if(i < s.length()-1){
                if (num == 1 && "VX".indexOf(s.charAt(i+1)) != -1){
                    result -= num;
                    continue;
                }

                else if (num == 10 && "LC".indexOf(s.charAt(i+1)) != -1){
                    result -= num;
                    continue;
                }
                else if (num == 100 && "DM".indexOf(s.charAt(i+1)) != -1){
                    result -= num;
                    continue;
                }
            }

            result += num;

        }
        return result;
    }
}

class Solution {
    public int romanToInt(String s) {

        int result = 0;

        for(int i = 0; i< s.length(); i++){
            int num = s.charAt(i);
            switch(num){
                case 'I':
                    num = 1;
                    break;
                case 'V':
                    num = 5;
                    break;
                case 'X':
                    num = 10;
                    break;
                case 'L':
                    num = 50;
                    break;
                case 'C':
                    num = 100;
                    break;
                case 'D':
                    num = 500;
                    break;
                case 'M':
                    num =1000;
                    break;
                default: num = 0;
            }

            if(i < s.length()-1){
                if (num == 1 && "VX".indexOf(s.charAt(i+1)) != -1){
                    result -= num;
                    continue;
                }

                else if (num == 10 && "LC".indexOf(s.charAt(i+1)) != -1){
                    result -= num;
                    continue;
                }
                else if (num == 100 && "DM".indexOf(s.charAt(i+1)) != -1){
                    result -= num;
                    continue;
                }
            }
            result += num;
        }
        return result;
    }
}
